	var navLeftOffset=0;
	var movingSteps=120;

	function navIconsDecider(){
		ulWidth= $(".navbar-v3 .desktop .nav-slider ul.main-menu-item").width();
		navBarDesktopWidth= $(".navbar-v3 .desktop").width();

		if((ulWidth+parseInt($(".navbar-v3 .desktop .nav-slider ul.main-menu-item").css("left")))>navBarDesktopWidth){
			$(".navButtonRight").show();
			$(".parent_navButtonRight").show();
		}else{
			$(".navButtonRight").hide();
			$(".parent_navButtonRight").hide();
		}

		if(parseInt($(".navbar-v3 .desktop .nav-slider ul.main-menu-item").css("left").replace("px",""))<0){
			$(".navButtonLeft").show();
			$(".parent_navButtonLeft").show();
		}else{
			$(".navButtonLeft").hide();
			$(".parent_navButtonLeft").hide();
		}
	}

