	$(document).on('click', '.navButtonLeft', function(event){
		//console.log("AAARASA",ulWidth+navLeftOffset);
		if((ulWidth+parseInt($(".navbar-v3 .desktop .nav-slider ul.main-menu-item").css("left")))<=0)return;
		//navLeftOffset+=movingSteps;
		$(".navbar-v3 .desktop .nav-slider ul.main-menu-item").animate({left:parseInt($(".navbar-v3 .desktop .nav-slider ul.main-menu-item").css("left"))+movingSteps},{easing:'swing',complete: function(){
        	navIconsDecider();
    	}});
		//navIconsDecider();
	});
	$(document).on('click', '.navButtonRight', function(event){
		if((ulWidth+parseInt($(".navbar-v3 .desktop .nav-slider ul.main-menu-item").css("left")))<=navBarDesktopWidth)return;
		$(".navbar-v3 .desktop .nav-slider ul.main-menu-item").animate({left:parseInt($(".navbar-v3 .desktop .nav-slider ul.main-menu-item").css("left"))-movingSteps},{easing:'swing',complete: function(){
