document.addEventListener('DOMContentLoaded', () => {
    // Collect all account IDs first and load their scripts
    const videoPlayers = document.querySelectorAll(
        "#article-images-block-block_a4150b811874a3c5ec21c3f6bbb204bd .article-images-block .article-images-block-img-wrapper video"
    );
    const accountIds = new Set();

    videoPlayers.forEach(player => {
        const accountId = player.getAttribute('data-account');
        if (accountId) {
            accountIds.add(accountId);
        }
    });

    // Load all required Brightcove scripts
    accountIds.forEach(accountId => {
        if (!document.querySelector(`script[src*="${accountId}"]`)) {
            let script = document.createElement("script");
            script.src = `https://players.brightcove.net/${accountId}/default_default/index.min.js`;
            document.body.appendChild(script);
        }
    });

    // Initialize player controls after scripts are loaded
    [].slice.call(document.querySelectorAll(
            "#article-images-block-block_a4150b811874a3c5ec21c3f6bbb204bd .article-images-block .article-images-block-img-wrapper"
        ))
        .forEach((block) => {
            const trigger = block.querySelector('.btn-play');
            const img = block.querySelector('.lazyload');
            const player = trigger.nextElementSibling;

            function initializeBrightcovePlayer(videoId) {
                if (window.videojs) {
                    let bcPlayer = videojs(`video-${videoId}`);

                    // Wait for player to be ready
                    bcPlayer.ready(function() {
                        // Play video directly
                        bcPlayer.play();

                        // Handle any play errors
                        bcPlayer.on('error', function() {
                            console.warn('Video playback error:', bcPlayer.error());
                        });
                    });
                } else {
                    setTimeout(() => initializeBrightcovePlayer(videoId), 100);
                }
            }

            function resetZIndexes() {
                document.querySelectorAll('.article-images-block-img-wrapper').forEach(wrapper => {
                    const videoContainer = wrapper.querySelector('.video-js')?.parentNode;
                    if (videoContainer) {
                        videoContainer.style.zIndex = '1';
                    }
                });
            }

            trigger.addEventListener('click', (event) => {
                event.preventDefault();

                if (player.tagName === 'IFRAME') {
                    player.src = player.dataset.src;
                    return false;
                }

                if (player.tagName === 'VIDEO') {
                    const videoId = player.getAttribute('data-video-id');

                    // Reset z-indexes before showing new player
                    resetZIndexes();

                    // Hide play button
                    trigger.style.display = 'none';
                    block.style.height = img.offsetHeight + 'px';
                    img.style.display = 'none';
                    if (player.parentNode) {
                        player.parentNode.style.zIndex = 2;
                    }
                    player.focus();

                    // Initialize player with shorter timeout since scripts are preloaded
                    setTimeout(() => {
                        initializeBrightcovePlayer(videoId);
                    }, 100);
                }
                return false;
            });

            // Keep keyboard accessibility
            trigger.addEventListener('keydown', (event) => {
                if (event.keyCode === 13 || event.keyCode === 32) {
                    event.preventDefault();
                    trigger.click();
                }
            });
        });
});

