function getExternalLinkContent(link) {
    var wildCardLinks = [];

    for (var i = 0; i < configuredExternalLinks.length; i++) {
        var configuredExternalLink = configuredExternalLinks[i];

        for (var j = 0; j < configuredExternalLink.domains.length; j++) {
            var domain = configuredExternalLink.domains[j];

            if (domain === '*') {
                wildCardLinks.push(configuredExternalLink);
            }

            if (link.indexOf(domain) !== -1) {
                return configuredExternalLink;
            }
        }
    }

    if (!wildCardLinks.length) {
        return null;
    }

    return wildCardLinks[0];
}

function stripHttpsWWW(link){
    return link.replace(/^(https?:\/\/)?(www\.)?/i, '');
}

function shouldAllowDomain(link) {
    //get the domain text without the http or https or www.
    var urlParts = stripHttpsWWW(link).split('/');

    if (!urlParts.length) {
        return false;
    }

    var domain = urlParts[0];
    var currentDomain = stripHttpsWWW(document.location.hostname);

    // Allow if the domain matches the current domain
    if (domain === currentDomain) {
        return true;
    }

    // Check if the domain is in the allowed domains
    if (allowedDomains.some(function(allowedDomain) {

        // Check if it's a protocol (e.g., tel:, mailto:, javascript:)
        if (link.startsWith(allowedDomain)) {
            return true;
        }

        allowedDomain = stripHttpsWWW(allowedDomain);
        return domain === allowedDomain || domain.endsWith(`.${allowedDomain}`);
    })) {
        return true;
    }

    return false;
}

function isSubdomain(domain) {
    return domain.split('.').length !== 2;
}

