        }
        el.setAttribute('webkit-playsinline', '')
        el.setAttribute('playsinline', 'true')
        lazy_videos.splice(index, 1)
        lazy_videos = lazy_videos.length === 0 ? null : lazy_videos
      }
    })
  })
}

