                        /* Styles for new rating block start */
            .ncr-rating__box ul {
                margin: 0;
                padding: 0;
                list-style: none;
                }

                .ncr-rating__box li {
                margin: 0;
                padding: 0;
                }

                .ncr-rating__box p {
                margin: 0;
                }

                .ncr-rating__box {
                margin-top: 2rem;
                }

                .ncr-rating__block {
                display: flex;
                }

                .ncr-rating__preview,
                .ncr-rating__content {
                background-color: rgba(247, 247, 247, 1);
                height: 100%;
                }

                .ncr-rating__preview {
                    width: 100%;
                padding: 10px;
                border-radius: 32px;
                position: relative;
                display: flex;
                align-items: center;
                overflow: hidden;
                }

                .ncr-rating__preview::after {
                content: "";
                position: absolute;
                right: 0px;
                top: 0;
                bottom: 0;
                width: 2px;
                background-image: repeating-linear-gradient(
                    to bottom,
                    #ffffff 0px,
                    #ffffff 10px,
                    /* довжина штриха */ transparent 10px,
                    transparent 15px /* відстань між штрихами */
                );
                }

                .ncr-rating__badge {
                position: absolute;
                left: 20px;
                top: -16px;
                z-index: 2;
                }

                .ncr-rating__image-wrap{
                    padding:12px;
                    min-width: 100%;
                    max-width:100%;
                    border-radius: 24px;
                    overflow:hidden;
                    position: relative;
                    max-width: 232px;
                    height: 132px;
                    display: flex;
                    justify-content:center;
                    background-color:#ffffff;
                }

                .ncr-rating__image {
                    width: auto;
                    height: auto;
                    margin:auto;
                    max-width:100%;
                    max-height:100%;
                    object-fit: contain;
                }

                .ncr-rating__content {
                display: flex;
                padding: 18px 0;
                }

                .ncr-rating__bonus {
                width: 35.5%;
                }

                .ncr-rating__info {
                width: 37.5%;
                }

                .ncr-rating__refs {
                width: 27%;
                }

                .ncr-rating__content {
                border-radius: 32px;
                }

                .ncr-rating__content-item {
                padding: 0 24px;
                position: relative;

                display: flex;
                flex-direction: column;
                justify-content: center;
                }

                .ncr-rating__content-item:not(:last-child):after {
                content: "";
                position: absolute;
                right: 0;
                top: 0;
                bottom: 0;
                width: 2px;
                background-image: repeating-linear-gradient(
                    to bottom,
                    #ffffff 0px,
                    #ffffff 10px,
                    /* довжина штриха */ transparent 10px,
                    transparent 15px /* відстань між штрихами */
                );
                }

                .ncr-rating__bonus-title {
                background-color: rgba(0,0,0,0.08);
                color: rgba(0,0,0,1);
                max-width: fit-content;
                padding: 2px 12px;
                line-height: 1.5;
                border-radius: 12px;
                font-weight: 700;
                font-size: 16px;
                max-height: fit-content;
					margin-bottom: 0 !important;
                }

                .ncr-rating__bonus-inner {
                display: flex;
                justify-content: space-between;
                align-items: center;
                }

                .ncr-rating__bonus-list {
                margin-top: 8px;
                padding: 6px 16px;
                background-color: rgba(0,0,0,0.08);
                border-radius: 12px;
                overflow: hidden;
                position: relative;
                }

                .ncr-rating__bonus-list::before {
                content: "";
                position: absolute;
                left: 0;
                top: 0;
                bottom: 0;
                width: 4px;
                height: 100%;
                background-color: rgba(0,0,0,1);
                opacity: .1;
                }

                .ncr-rating__bonus-list p {
                font-weight: 600;
                font-size: 14px;
                line-height: 1.5;
					margin:0 !important;
                }

                .ncr-rating__bonus-list p b,
                .ncr-rating__bonus-list p strong {
                font-weight: 700;
                font-size: 16px;
                color: rgba(0,0,0,1);
                }

                .ncr-rating__list {
                padding: 16px 24px !important;
                font-size: 16px;
                font-weight: 600;
                position: relative;
                border-top-right-radius: 24px;
                border-bottom-right-radius: 24px;
                border-top-left-radius: 8px;
                border-bottom-left-radius: 8px;
                background-color: rgba(255, 255, 255, 1);
                overflow: hidden;
                height: 100%;
                display: flex;
                flex-direction: column;
                justify-content: center;
                }

                .ncr-rating__block--1 .ncr-rating__list{
                    background-color: rgba(0, 0, 0, 0.04);
                }

                .ncr-rating__box--no-bg  .ncr-rating__block--1 .ncr-rating__list{
                    background-color: rgba(255, 255, 255, 1);
                }


                .ncr-rating__list::before {
                position: absolute;
                width: 4px;
                left: 0;
                content: "";
                top: 0;
                height: 100%;
                background-color: rgba(0,0,0,1);
                }

                .ncr-rating__text {
                position: relative;
                padding-left: 36px;
                line-height: 1.33;
                padding-top: 2px;
                padding-bottom: 2px;
                }

                .ncr-rating__icon {
                    display: block;
                position: absolute;
                left: 0;
                top: 0;
                }

                .ncr-rating__item:not(:first-child) {
                margin-top: 16px;
                }

                .ncr-rating__item{
                    padding-left:0 !important;
                    margin-bottom: 0 !important;
                }
                .ncr-rating__item::before{
                    content: none !important;
                    display: none !important;
                }

                .ncr-rating__button {
                text-decoration: none !important;
                outline: none;
                width: 100%;
                text-align: center;
                display: flex;
                justify-content: center;
                align-items: center;
                line-height: 1.5;
                padding: 13px 13px;
                border-radius: 24px;
                font-size: 16px;
                font-weight: 700;
                transition: 0.3s ease-in-out;
                white-space: nowrap;
                }

                .ncr-rating__button:not(:first-child) {
                margin-top: 8px;
                }

                .ncr-rating__button--dark {
                background-color: rgba(0,0,0,1);
                color: #ffffff !important;
                border: 1px solid rgba(0,0,0,1);
                }

                .ncr-rating__button--light {
                color: #000000 !important;
                background-color: transparent;
                border: 1px solid rgba(0,0,0,1);
                }

                .ncr-rating__button:hover {
                color: #000000 !important;
                background-color: rgba(255, 255, 255, 1);
                box-shadow: 0px 0px 13px 3px rgba(0,0,0,0.15);
                -webkit-box-shadow: 0px 0px 13px 3px rgba(0,0,0,0.15);
                -moz-box-shadow: 0px 0px 13px 3px rgba(0,0,0,0.15);
                }

                .ncr-rating__button:active {
                transform: scale(0.95);
                }

                .ncr-rating__button-icon {
                margin-right: 12px;
                }

                .ncr-rating__num {
                position: absolute;
                color: #ffffff;
                font-size: 17px;
                font-weight: 700;
                left: 0;
                top: 6px;
                width: 37px;
                text-align: center;
                }

                .ncr-rating__bonus-count {
                --percent: 80; /* змінюємо відповідно до класу */
                --hue: calc(
                    (var(--percent) / 10) * 12
                ); /* 0 - червоний, 60 - жовтий, 120 - зелений */

                width: 48px;
                height: 48px;
                border-radius: 50%;
                background: conic-gradient(
                    hsl(var(--hue), 100%, 40%) calc(var(--percent) * 1%),
                    var(--bg) 0
                );
                display: flex;
                align-items: center;
                justify-content: center;
                font-weight: bold;
                color: #000000;
                font-size: 16px;
                padding: 5px;
                background-color: hsl(var(--hue), 100%, 85%);
                }

                .ncr-rating__bonus-count span {
                min-width: 40px;
                min-height: 40px;
                background-color: var(--bg, #fff);
                border-radius: 50%;
                display: flex;
                align-items: center;
                justify-content: center;
                }

                .ncr-rating-0 {
                --percent: 0;
                --bg: #fddede;
                }
                .ncr-rating-5 {
                --percent: 5;
                --bg: #fcd4d4;
                }
                .ncr-rating-10 {
                --percent: 10;
                --bg: #fbcaca;
                }
                .ncr-rating-15 {
                --percent: 15;
                --bg: #fbc0c0;
                }
                .ncr-rating-20 {
                --percent: 20;
                --bg: #fce0c8;
                }
                .ncr-rating-25 {
                --percent: 25;
                --bg: #fceac4;
                }
                .ncr-rating-30 {
                --percent: 30;
                --bg: #fff1cb;
                }
                .ncr-rating-35 {
                --percent: 35;
                --bg: #fff5c6;
                }
                .ncr-rating-40 {
                --percent: 40;
                --bg: #fff8c7;
                }
                .ncr-rating-45 {
                --percent: 45;
                --bg: #ffffcb;
                }
                .ncr-rating-50 {
                --percent: 50;
                --bg: #ffffcc;
