			.mf-form{
				display: flex;
    			flex-direction: column;
				padding: 0 20px;
				position: relative;
			}
			.mf-form__input{
				border: 2px solid var(--popup-color);
				border-radius: 8px;
				padding: 8px 12px;
				margin-bottom: 16px;
			}
			.mf-form__submit{
				background: var(--popup-color);
			border: 2px solid var(--popup-color);
			padding: 8px 12px;
			border-radius: 8px;
			color: #ffffff;
				transition: .3s ease-in-out;
				font-weight:600;
			}
			.mf-form__submit:hover{
				background:#ffffff;
				color: var(--popup-color);
			}

				.popup-form-wrap.loading .mf-form {
					pointer-events: none; 
					opacity: 0.5; 
				}

					.popup-form-wrap.loading .mf-form:before {
					content: '';
					position: absolute;
					top: 25%;
					left:0;
					right:0;
						margin: 0 auto;
					width: 40px;
					height: 40px;
					border: 4px solid #f3f3f3; /* Світлий колір для фону */
					border-top: 4px solid #3498db; /* Колір для анімації */
					border-radius: 50%;
					animation: spin 1s linear infinite;
					z-index: 999;
				}
				@keyframes spin {
					0% { transform: translate(-50%, -50%) rotate(0deg); }
					100% { transform: translate(-50%, -50%) rotate(360deg); }
				}
				.popup-form-wrap.loaded .mf-form {
					display: none;
				}

			.popup-form-wrap.loaded .mf-form:before{
				display:none;
				content:none;
			}

			.mf-form__success{
				display:none !important;
			}
			.popup-form-wrap.loaded .mf-form__success{
				display:block !important;
			}
			/* 	Styles for modal window form	end	 */

