    .comment-reply-link:hover {
        border-bottom: 1px solid transparent;
    }

    .comment-body {
        width: 100%;
        display: flex;
        flex-wrap: wrap;
        justify-content: space-between;
    }

    .comment-author {
        width: 48%;
    }

    .comment-meta {
        font-weight: 500;
        text-transform: uppercase;
        width: 48%;
        display: flex;
        justify-content: right;
        align-items: center;
    }

    .comment-meta a {
        margin-top: 0;
    }

    .comment-body p:not(.reply) {
        margin: 16px;
    }

    .comment-body p {
        width: 100%;
    }

    .comment-reply-title {
        display: flex;
        width: 100%;
    }

    .comment {
        padding: 16px;
        border-radius: 8px;
        margin: 1.5rem 0;
        position: relative;
        overflow: hidden;
    }

    .comment.thread-alt {
        margin-left: 2rem;
    }

    .comment-author cite {
        font-style: normal;
        font-weight: 500;
    }

    .comment-author span {
        display: none;
    }

    .comment-reply-title {
        font-weight: 500;
    }

    @media(max-width:1024px) {
        .custom-checkbox label:before {
            top: 2px;
        }
    }

    @media(max-width:767px) {
        .comment-author {
            font-size: 14px;
        }

        .comment-meta {
            font-size: 14px;
        }

        .comment-body p {
            font-size: 14px;
        }

        .comment {
            padding: 0;
        }

        .comment.thread-alt {
            margin-left: 1rem;
        }
    }

    @media(max-width:580px) {
        .comment-author img {
            width: 24px;
            height: 24px;
        }

        .comment-author {
            font-size: 11px;
        }

        .comment-meta {
            font-size: 11px;
        }

        .comment-body p:not(.reply) {
            margin-right: 0;
        }

        .comment-form-author input,
        .comment-form-email input {
            padding: 4px 8px;
            border-radius: 4px;
        }

        .comments textarea {
            padding: 4px 8px;
            border-radius: 4px;
        }

        .form-submit input {
            min-width: 100%;
        }

        .comment-respond {
            margin-top: 2rem;
        }
    }

    @media(max-width:440px) {
        .comment-body p:not(.reply) {
            margin: 12px 8px;
        }

        .comment-reply-link {
            font-size: 12px;
        }
    }

    @media(max-width:420px) {
        .comment-author {
            font-size: 10px;
        }

        .comment-meta {
            font-size: 10px;
        }
    }

    @media(max-width:379px) {

        .comment-author,
        .comment-meta {
            font-size: 9px;
        }

        .comment-author img {
            width: 20px;
            height: 20px;
        }
    }

