    justify-content: space-between;
    margin-bottom: 20px;
}
.custom-modal--bottom-text {
    color: #ffffff;
    font-size: 20px;
    font-style: italic;
    font-weight: 700;
    margin-bottom: 34px;
    width: 260px;
}
.custom-modal--bottom-text span{
    color: #005d5b;
}
.custom-modal--link {
    text-decoration: none;
    background: #005d5b;
    border-radius: 8.4px;
    color: #FFFFFF;
    display: inline-block;
    font-size: 16px;
    font-style: italic;
    font-weight: 900;
    line-height: 1;
    min-width: 240px;
    padding: 16.5px 20px;
    text-align: center;
    text-transform: uppercase;
}
.custom-modal img{
    max-width:100%;
    height: auto;
}
.custom-modal--logos>div {
    max-width: 115px;
}
.custom-modal--logos svg {
    margin: 0 15px;
}
.custom-modal button {
    cursor: pointer;
}
.custom-modal button {
    -webkit-appearance: button;
    background-color: transparent;
    background-image: none;
}
.custom-modal button {
    text-transform: none;
}
.custom-modal button {
    font-family: inherit;
    font-feature-settings: inherit;
    font-variation-settings: inherit;
    font-size: 100%;
    font-weight: inherit;
    line-height: inherit;
    color: inherit;
    margin: 0;
    padding: 0;
}
.custom-modal--top span {
    color: #005d5b;
    font-weight: 900;
}
@media (max-width: 600px){
    .custom-modal--inner {
        align-items: center;
        flex-direction: column-reverse;
        margin-left: 24px;
        margin-right: 24px;
        padding: 42px 36px;
        width: 100%;
    }
    .custom-modal--left {
        min-width: auto;
        text-align: center;
        width: auto;
    }
    .custom-modal--bottom-text {
        margin-left: auto;
        margin-right: auto;
    }
    .custom-modal--right {
        margin-bottom: 30px;
        width: 143px;
    }
}
