.popup {
    display: none;
    position: fixed;
    top: 0;
    left: 0;
    width: 100vw;
    height: 100vh;
    opacity: 0;
    transition: opacity 0.5s ease-in-out;
}

.popup .popup_bg {
    position: absolute;
    display: none;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(255, 255, 255, 0.8);
    backdrop-filter: blur(10px);
    opacity: 0;
    transition: opacity 0.5s ease-in-out;
}

.popup .popup_cont {
    position: absolute;
    display: block;
    left: calc(50% - 200px);
    top: calc(50% - 200px);
}

.popup .popup_cont img.popup_logo {
    position: absolute;
    top: -150px;
    opacity: 0;
    transition: opacity 0.5s ease-in-out, top 0.5s ease-in-out;
}

