.t-widget-translate {
	position: fixed;
	right: 15px;
	bottom: 15px;
	z-index: 2147483647;
	font-family: 'TildaSans',Arial,sans-serif;
}

.t-widget-translate-wrapper {
	overflow: hidden;
	border-radius: 30px;
	background-color: #fff;
	border: 1px solid rgba(113, 113, 113, .2);
}

details.t-widget-translate-wrapper > summary.t-widget-translate__current-lang-wrapper::-webkit-details-marker {
	display: none;
}

.t-widget-translate__languages {
	position: absolute;
	right: 0;
	bottom: calc(100% + 10px);
	display: flex;
	align-items: center;
	flex-direction: column;
	font-size: 14px;
	font-weight: 300;
	background-color: #fff;
	border-radius: 3px;
	margin: 0;
	padding: 4px;
	list-style: none;
	overflow: hidden;
	box-shadow: 0px 4px 4px rgba(0, 0, 0, .1);
	border: 1px solid rgba(113, 113, 113, .2);
	animation: t-widget-translate__languages--open 0.3s ease-in-out;
}

.t-widget-translate__languages li {
	width: 100%;
}

.t-widget-translate__languages--close {
	display: none;
}

@keyframes t-widget-translate__languages--open {
	0% {
		opacity: 0;
	}
	100% {
		opacity: 1;
	}
}

.t-widget-translate__lang {
	display: flex;
	align-items: center;
	text-decoration: none;
	color: #000;
	padding: 7px;
}

.t-widget-translate__lang--current {
	font-weight: 500;
	cursor: default;
}

.t-widget-translate__lang__code {
	width: 20px;
	padding-right: 5px;
	border-right: 1px solid rgba(113, 113, 113, .2);
}

.t-widget-translate__lang__name {
	padding-left: 5px;
}

.t-widget-translate__current-lang__icon {
	display: flex;
	justify-content: center;
	align-items: center;
	width: 100%;
	height: 100%;
	font-size: 13px;
	font-weight: 500;
	color: #000;
	gap: 10px;
	user-select: none;
}

