	/*get the submit button*/const submitBtn = $('.requestdemo');
  /*on submit button click*/submitBtn.click(()=>{
  // get the email field
  const email = $('.homepage_email');
  // split email at '@' character to get domain
  const domainPart = email.val().split('@')[1]; 
  // if the domain exists in the invalidDomains array
  if(invalidDomains.indexOf(domainPart) !== -1) {
    // clear email field
    email.val('');
    // add a 'use business mail' placeholder
    email.attr('placeholder','Please enter a business email');
    // prevent form submission
    return false;
   }
    else{	// else if email is not invalid
      // submit form
      return true;
    }
	});

});

   let logos__showcase = document.querySelectorAll(".logos__showcaseitems");
   document
     .querySelectorAll(
       ".logos__showcaseitems .attachment-thumbnail:first-of-type"
     )
     .forEach(function (el, i) {
       el.classList.add("on");
     });
   let durationTemp = 8000;
   let delayTemp = 3000;

   function LogoIndex(value) {
     return logos__showcase[value];
   }
   function logoImage(value) {
       if (value.querySelector(".attachment-thumbnail.on + img")) {
         value
           .querySelector(".attachment-thumbnail.on + img")
           .classList.add("on");
         value
           .querySelectorAll(".attachment-thumbnail.on")[0]
           .classList.remove("on");
       } else {
         value
           .querySelector(".attachment-thumbnail.on")
           .classList.remove("on");
         value
           .querySelectorAll(".attachment-thumbnail")[0]
           .classList.add("on");
       }
   }
		setInterval(function () {
     logoImage(LogoIndex([0]));
   }, durationTemp);

   setTimeout(function () {
    setInterval(function () {
     logoImage(LogoIndex([1]));
     }, durationTemp);
   }, delayTemp * 2);

   setTimeout(function () {
      setInterval(function () {
     logoImage(LogoIndex([2]));
         }, durationTemp);
   }, delayTemp);

   setInterval(function () {
      setInterval(function () {
     logoImage(LogoIndex([3]));
         }, durationTemp);
