            },
            onClick: function() {} // Callback after click
        }).showToast();
    })

    Livewire.on('success', message => {
        Toastify({
            text: message,
            duration: 6000,
            newWindow: true,
            close: true,
            gravity: "top", // `top` or `bottom`
            position: "right", // `left`, `center` or `right`
