$(document).ready(function() {

    // Hide all featured courses initially except the first four

    $(".featured-courses .course").hide(); // Hide all elements with the class "featured"
    $(".featured-courses .featured").slice(0, 4).show();

    $(".courses-filter").on("click", "a", function() {
        var filter = $(this).attr("data-filter");

        $(".featured-courses .course").hide();
        // Show the courses that match the filter and are within the first four
        $(".featured-courses .course" + filter).slice(0, 4).show();

        // Remove active class from all filter buttons
        $(".courses-filter a").removeClass("active");

        // Add active class to the clicked filter button
        $(this).addClass("active");

        return false; // Prevent default behavior of the link
    });

