        pause: "false"
    });
});

function deferPosterImage() {
    const instanceElement = document.querySelector('[data-vbg]');
    const posterUrl = instanceElement.getAttribute('data-poster-src');
    const observerOptions = {
        root: null,
        threshold: 0.5, // Adjust as needed
    };

    const observer = new IntersectionObserver((entries, observer) => {
        entries.forEach(entry => {
            if (entry.isIntersecting) {
                // Set the actual poster image once in view
                instanceElement.style.backgroundImage = `url(${posterUrl})`;

                // Now initialize the background video
                jQuery('[data-vbg]').youtube_background();

                // Clean up observer after image is loaded
                observer.unobserve(entry.target);
            }
        });
    }, observerOptions);

    // Start observing the instance element
    observer.observe(instanceElement);
}

// Call deferPosterImage on page load
$(document).ready(function() {
    deferPosterImage();
});



function isScrolledIntoView(elem) {
    var docViewTop = $(window).scrollTop();
    var docViewBottom = docViewTop + $(window).height();

    var elemTop = $(elem).offset().top;
    var elemBottom = elemTop + $(elem).height() - 300;

    return ((elemBottom <= docViewBottom) && (elemTop >= docViewTop));
}

