const videoBackgrounds = VIDEO_BACKGROUNDS;

// Get the element
const instanceElement = document.querySelector('[data-vbg]');

// Get the instance by UID
const instance = videoBackgrounds.get(instanceElement);

// Immediately check if video is in view when the page loads
function checkAndHandleVideoPlayback() {
    if (isScrolledIntoView(instanceElement)) {
        instance.play();
    } else {
        instance.pause();
    }
}

// Run the check on page load
$(document).ready(function() {
    checkAndHandleVideoPlayback();
});

$(window).scroll(function() {
    // Check on scroll
    checkAndHandleVideoPlayback();
});


const playToggleElem = document.getElementById('play-toggle');
const playIcon = document.getElementById('play-icon');
const pauseIcon = document.getElementById('pause-icon');

const updateIcon = (isPlaying) => {
    if (isPlaying) {
        playIcon.style.display = 'none';
        pauseIcon.style.display = 'inline';
    } else {
        playIcon.style.display = 'inline';
        pauseIcon.style.display = 'none';
    }
};

if (instance) {
    updateIcon(instance.currentState === 'playing');

    instanceElement.addEventListener('video-background-play', () => {
        updateIcon(true);
        playToggleElem.setAttribute('aria-pressed', 'true');
    });

    instanceElement.addEventListener('video-background-pause', () => {
        updateIcon(false);
        playToggleElem.setAttribute('aria-pressed', 'false');
    });

    // Handle visibility change
    document.addEventListener('visibilitychange', () => {
        if (document.hidden) {
            instance.pause(); // Pause the video when the tab is hidden
            console.log('Tab is hidden, video paused');
        } else {
            console.log('Tab is visible');
            // Optionally resume video when the tab is visible
            // instance.play();
        }
    });

    // Handle page hide/show (especially for mobile)
    window.addEventListener('pagehide', () => {
        instance.pause();
        console.log('Page is hidden, video paused');
    });

    window.addEventListener('pageshow', () => {
        console.log('Page is shown, video can resume');
        // Optionally resume video on pageshow
        // instance.play();
    });

    playToggleElem.addEventListener('click', () => {
        if (instance.currentState === 'playing') {
            instance.pause();
        } else {
            instance.play();
        }
    });

} else {
    console.error('Video background instance not found.');
}

function animateStatsOnScroll() {
    var x = 0; // Ensure animation triggers only once

    function checkAndAnimateStats() {
        var oTop = $('.statistics').offset().top - $(window).scrollTop();

        // Check if the element is in the viewport
        if (x === 0 && oTop <= $(window).height() && oTop >= 0) {
            $('.stats-counter-value').each(function() {
                var $this = $(this),
                    countTo = parseInt($this.attr('data-count').replace(/,/g, ''),
                        10); // Remove commas and parse as number

                $({
                    countNum: 0
                }).animate({
                    countNum: countTo
                }, {
                    duration: 2000,
                    easing: 'swing',
                    step: function(now) {
                        $this.text(Math.floor(now).toLocaleString()); // Add commas for display
                    },
                    complete: function() {
                        $this.text(countTo
                            .toLocaleString()); // Ensure final value is formatted with commas
                    }
                });
            });
            x = 1; // Ensure the animation runs only once
        }
    }

    // Check stats on scroll
    $(window).scroll(checkAndAnimateStats);

    // Also check stats on page load
    $(document).ready(function() {
        checkAndAnimateStats();
    });
}

animateStatsOnScroll();




