          const { data } = err?.response;
          if (data?.Status === "Error") {
              Toastify({
                  text: data?.ExceptionMessage,
                  duration: 6000,
                  newWindow: true,
                  close: true,
                  gravity: "top",
                  position: "right",
                  stopOnFocus: true,
                  style: {
                      background: "linear-gradient(to right, #FF0000, #FF7878)",
                  },
              }).showToast();
          }
      }).finally(() => {
          setTimeout(() => {
              window.location.reload();
          }, 5000);
      });
    }      
});

