styles:
#cookieWarnBox {transition:all 0.4s ease-in-out;position:fixed;z-index:999999;bottom:-20px;left:0;right:0;opacity:0;text-align:center;padding:10px;background-color:#212121} #cookieWarnBox .btn {white-space:nowrap} #cookieWarnBox .reject_more {padding:0px 10px;display:none;} #cookieWarnBox.reject .reject_more {display:block;} #cookieWarnBox.loaded {opacity:0.9;bottom:0px} #cookieWarnBox.closed {display:none;} #cookieWarnBox {font-family: Verdana;line-height:24px;color:#f1f1f1;font-size:14px;} #cookieWarnBox .btn {text-transform:uppercase;cursor:pointer;background-color:#f1f1f1;color:#659fda;padding:3px 14px;margin-left:10px;} #cookieWarnBox .btn:hover {background-color:#ffffff;color:#4d78a5;} #cookieWarnBox a {text-decoration:none;color:#659fda}
