styles:
	.adthrive-ad {
		margin-top: 10px;
		margin-bottom: 10px;
		text-align: center;
		overflow-x: visible;
		clear: both;
		line-height: 0;
	}
	#shotkit-post-content-row .fl-col-small .fl-col-group:has(.fl-col-content .fl-module-subscribe-form) {
  min-height: 2000px;
}

#shotkit-post-content-row .fl-col-small .fl-col-group:has(.fl-col-content .fl-module-subscribe-form) > div {
  position: sticky;
  top: 0px;
}

.mediavine-sidebar {
  position: relative !important;
}

footer {
  margin-bottom: 50px;
}

@media only screen and (min-width: 768px) {
  footer {
    margin-bottom: 90px;
  }
}

/* Top Center White Background */
.adthrive-collapse-mobile-background {
background-color: #fff!important;
}
.adthrive-top-collapse-close > svg > * {
stroke: black;
font-family: sans-serif;
}
.adthrive-top-collapse-wrapper-video-title,
.adthrive-top-collapse-wrapper-bar a a.adthrive-learn-more-link {
color: black !important;
}
/* END top center white background */

