/* If there is a fixed header */
.header {
	position: relative;
	top: auto;
}
.main {
	padding: 0;
}

/* Alert banner styling */
.alert-banner {
	display: none;
	background-color: #003F72;
	color: #FFFFFF;
	left: 0;
	margin-top: 0;
	padding: 2rem 1rem 0.8rem;
	position: relative;
	text-align: center;
	top: 0;
	width: 100%;
}
.alert-banner * {
	margin-top: 0;
}
.alert-banner.lightblue {
	background-color: #84A2BD;
}
.alert-banner.red {
	background-color: #DC0A2C;
}
.alert-banner.gray {
	background-color: #D0D2CF;
	color: #000000;
}

.alert-banner h3 {
	font-size: 1.2em;
	padding-bottom: 0.5rem;
}
.alert-banner p {
	margin: 0 auto;
	padding: 0;
	width: 90%;
}
.alert-banner p a {
	font-weight: bold;
	text-decoration: underline;
}
.close-icon {
	border: solid 2px #FFFFFF;
	color: #FFF;
	border-radius: 100%;
	display: block;
	font-family: sans-serif;
	font-weight: normal;
	font-size: 17px;
	height: 30px;
	margin: 1rem auto;
	text-align: center;
	width: 30px;
	line-height: 28px;
}

@media screen and (min-width: 960px) {
	.alert-banner {
		padding: 0.8rem 1rem;
	}
	.alert-banner p {
		padding: 0 2.5rem;
		width: 70%;
	}
	.close-icon {
		margin: 0;
		position: absolute;
		right: 2rem;
		top: 50%;
		transform: translate(0, -50%);
	}
}
