  :root {
    --font-body-family: Futura, sans-serif;
    --font-body-style: normal;
    --font-body-weight: 400;

    --font-heading-family: Futura, sans-serif;
    --font-heading-style: normal;
    --font-heading-weight: 400;

    --font-button-family: Futura, sans-serif;
    --font-button-style: normal;
    --font-button-weight: 400;

    --font-heading-letter-spacing: 0;
    --font-heading-text-transform: uppercase;

    --font-body-scale: 1.0;
    --font-heading-scale: 1.0;

    --font-weight-normal: 400;
    --font-weight-bold: 700;
    --font-weight-light: ;

    --line-height-extra-small: 1;
    --line-height-small: 1.3;
    --line-height-medium: 1.6;

    --letter-spacing-extra-small: .05rem;
    --letter-spacing-small: .1rem;
    --letter-spacing-medium: .2rem;

    --h1-multiplier: 3.2rem;
    --h2-multiplier: 2.4rem;
    --h3-multiplier: 1.8rem;
    --h4-multiplier: 1.6rem;
    --h5-multiplier: 1.4rem;
    --h6-multiplier: 1.2rem;

    --font-size-extra-small: 1rem;
    --font-size-small: 1.2rem;
    --font-size-medium: 1.4rem;
    --font-size-large: 1.6rem;
    --font-size-extra-large: 1.8rem;

    --font-size-static-extra-small: 1rem;
    --font-size-static-small: 1.2rem;
    --font-size-static-medium: 1.4rem;
    --font-size-static-large: 1.6rem;
    --font-size-static-extra-large: 1.8rem;

    /* Typography */
    --color-heading-text: #111111;
    --color-heading-text-rgb: 17, 17, 17;
    --color-body-text: #5e5a59;

    /* Buttons and links */
    --color-button-outlined-text: #111111;
    --color-button-outlined-background: rgba(0,0,0,0);
    --color-button-filled-text: #ffffff;
    --color-button-filled-background: #111111;

    --color-button-background: transparent;
    --color-button-outline: #ffffff;
    --color-button-text: #ffffff;

    --color-form-text: #111111;
    --color-form-button-text: #ffffff;

    --button-border-radius: 0;
    --button-text-transform: uppercase;
    --input-border-radius: 0;

    /* Other elements */
    --color-link: #333232;
    --color-link-text: #111111;
    --color-default-link-text: #111111;
    --color-tag-text: #111111;
    --color-tag-background: #ffffff;
    --color-border-elements: #e6e2e1;
    --color-cart-number-text: #111111;
    --color-shipping-bar-progress: #f4f4f4;

    /* Backgrounds */
    --color-body-background: #ffffff;
    --color-image-background: #f4f4f4;
    --color-body-background-transparent-50: rgba(255, 255, 255, 0.5);
    --color-popup-background: #ffffff;

    /* Background Colors */
    --color-background-primary: #ffffff;
    --color-background-inverse: #131313;
    --color-background-light: #f4f4f4;
    --color-background-dark: #e3adec;
    --color-background-accent-1: #d3a0c9;
    --color-background-accent-2: #ffffff;

    /* Text Colors */
    --color-text-primary: #111111;
    --color-text-secondary: #432818;
    --color-text-inverse: #ffffff;

    /* Text default */
    --color-heading-text-default: #111111;
    --color-body-text-default: #5e5a59;

    --color-link-text-default: #111111;
    --color-default-link-text-default: #111111;

    /* Text secondary */
    --color-heading-text-secondary: #432818;
    --color-body-text-secondary: #432818;

    /* Text inverse */
    --color-heading-text-inverse: #ffffff;
    --color-body-text-inverse: #ffffff;

    --color-link-text-inverse: #ffffff;
    --color-default-link-text-inverse: #ffffff;

    /* Default section style */
    --color-default-background: #ffffff;
    --color-default-image-background: #f4f4f4;
    --color-default-border-elements: #e6e2e1;

    /* Secondary section style */
