    /* Search container */
    .search-container {
        position: relative;
        display: flex;
        justify-content: right;
        width: 100%; /* Full width */
        z-index: 9999; /* Ensure it's above all content */
    }

    /* Hide search box initially */
    .search-box {
        display: none;
        position: absolute;
        top: 40px;
        left: 0;
        right: 0;
        z-index: 1000; /* High z-index */
        background: white;
        padding: 10px;
        border-radius: 5px;
        box-shadow: 0px 4px 6px rgba(0, 0, 0, 0.2);
        width: 100%; /* Full-width search */
        max-width: 100%;
    }

    /* Search icon button */
    .search-icon {
        background-color: #283779;
        color: white;
        border: none;
        padding: 4px 8px; /* Smaller padding */
        border-radius: 50%;
        cursor: pointer;
        font-size: 14px; /* Smaller icon size */
    }

    /* Style search input */
    input.gsc-input {
        height: 40px !important; /* Bigger input */
        font-size: 16px !important;
        padding: 0 !important; /* Remove padding */
        background-color: #fff !important;
        color: #333 !important;
        border: 1px solid #283779 !important;
        border-radius: 5px !important;
        width: 100% !important; /* Full width */
        max-width: 100%;
    }

    /* Style search button */
    button.gsc-search-button {
        height: 40px !important;
        font-size: 16px !important;
        padding: 8px 12px !important;
        background-color: #283779 !important;
        color: white !important;
        border: none !important;
        border-radius: 5px !important;
    }

    /* Hover effect */
    button.gsc-search-button:hover {
        background-color: #1e2b5a !important;
    }

    /* Remove any color styling from Google's search results */
    .gsc-results .gs-title,
    .gsc-results .gs-snippet,
    .gsc-results .gsc-thumbnail-inside,
    .gsc-results .gsc-cursor-page {
        color: inherit !important; /* Remove all color */
        background-color: transparent !important; /* Remove background color */
    }

    /* Remove any borders or additional styling */
    .gsc-results .gsc-cursor-page {
        border: none !important;
    }

    /* Responsive design adjustments */
    @media (max-width: 768px) {
        .search-container {
            padding: 0 10px;
        }

        .search-icon {
            font-size: 12px; /* Even smaller icon */
            padding: 4px 8px;
        }

        input.gsc-input {
            font-size: 14px !important;
        }

        button.gsc-search-button {
            font-size: 14px !important;
        }

        .search-box {
            top: 50px; /* Adjust position for smaller screens */
            left: 5%;
            right: 5%;
        }
    }

    @media (max-width: 480px) {
        .search-icon {
            font-size: 10px; /* Smaller icon size */
            padding: 3px 6px;
        }

        input.gsc-input {
            font-size: 14px !important;
        }

        button.gsc-search-button {
            font-size: 14px !important;
        }

        .search-box {
            top: 45px; /* Adjust position further for small screens */
            left: 2%;
            right: 2%;
        }
    }

