.awesomplete [hidden] {
    display: none;
}

.awesomplete .visually-hidden {
    position: absolute;
    clip: rect(0, 0, 0, 0);
}

.awesomplete {
    display: inline-block;
    position: relative;
	 min-width:90%;
	 max-width:100%;
}

.awesomplete > input {
    display: block;
}

.awesomplete > ul {
    position: absolute;
    left: 0;
    z-index: 1;
    min-width: 100%;
    box-sizing: border-box;
    list-style: none;
    padding: 0;
    margin: 0;
    background: #fff;
}

.awesomplete > ul:empty {
    display: none;
}

.awesomplete > ul {
	border-radius: .3em;
	margin: .2em 0 0;
	background: hsla(0,0%,100%,.9);
	background: linear-gradient(to bottom right, white, hsla(0,0%,100%,.8));
	border: 1px solid rgba(0,0,0,.3);
	box-shadow: .05em .2em .6em rgba(0,0,0,.2);
	text-shadow: none;
}

@supports (transform: scale(0)) {
	.awesomplete > ul {
		transition: .3s cubic-bezier(.4,.2,.5,1.4);
		transform-origin: 1.43em -.43em;
	}

	.awesomplete > ul[hidden],
	.awesomplete > ul:empty {
		opacity: 0;
		transform: scale(0);
		display: block;
		transition-timing-function: ease;
	}
}

	/* Pointer */
	.awesomplete > ul:before {
		content: "";
		position: absolute;
		top: -.43em;
		left: 1em;
		width: 0; height: 0;
		padding: .4em;
		background: white;
		border: inherit;
		border-right: 0;
		border-bottom: 0;
		-webkit-transform: rotate(45deg);
		transform: rotate(45deg);
	}

	.awesomplete > ul > li {
		position: relative;
		padding: .2em .5em;
		cursor: pointer;
		color:#000;
	}

	.awesomplete > ul > li:hover {
		background: hsl(200, 40%, 80%);
		color: black;
	}

	.awesomplete > ul > li[aria-selected="true"] {
		background: hsl(205, 40%, 40%);
		color: white;
	}

		.awesomplete mark {
			background: hsl(65, 100%, 50%);
		}

		.awesomplete li:hover mark {
			background: hsl(68, 100%, 41%);
		}

		.awesomplete li span {
			color:#999;
		}

		.awesomplete li[aria-selected="true"] mark {
			background: hsl(86, 100%, 21%);
			color: inherit;
		}


	 #slider {
    clear: both;
    font-family: Futura-Medium,Futura,"Lucida Grande",Segoe UI,Calibri,"serif";
    padding: 4em 0 3em 0;
	 margin: 3em 0 0 0;
  }

  #slider > .title {
    text-align: center;
    color: white;
    font-size: 32px;
    font-weight: bold;
    margin: 0;
    padding: 0 16px;
  }

  #slider .content {
    position: relative;
  }

  #slider .track {
    margin: 32px 0 0;
  }

  #slider button {
    position: absolute;
    top: 108px;
    background: none;
    border: none;
    -webkit-appearance: none;
    margin: 0;
    padding: 0;
    cursor: pointer;
  }

  #slider button.prev {
    left: 48px;
  }

  #slider button.next {
    right: 48px;
  }

  .hotel-card {
    box-sizing: border-box;
    max-width: 320px;
    margin: 32px 16px;
    background: white;
    padding: 16px 16px 32px 16px;
	 border-radius:15px;
  }

	.hotel-card h3 a { color:#fff;text-decoration:none;}

  .hotel-card img {
    height: 220px;
    width: 100%;
    object-fit: cover;
	 border-radius:8px;
  }

  .hotel-card .title {
    font-size: 22px;
    font-weight: bold;
    margin: 16px 0 0;
  }

  .hotel-card .location {
    font-size: 18px;
    font-style: italic;
    margin: 8px 0 0;
  }

  .hotel-card .description {
    margin: 12px 0 0;
	 font-family: Merriweather, Georgia, serif;
    font-size: 16px;
    line-height: 23px;
  }

  @media (min-width: 352px) {
    #slider button.prev {
      left: calc(50% - 128px);
    }

    #slider button.next {
      right: calc(50% - 128px);
    }

    .hotel-card {
      margin-left: auto;
      margin-right: auto;
    }
  }

  @media (min-width: 480px) {
    #slider button.prev {
      left: calc(50% - 224px);
    }

    #slider button.next {
      right: calc(50% - 224px);
    }
  }

  @media (min-width: 800px) {
    #slider .track > * > *:nth-child(odd) .slide {
      padding-right: 0;
    }

    #slider .track > * > *:nth-child(odd) .hotel-card {
      margin-right: 16px;
    }

    #slider .track > * > *:nth-child(even) .slide {
      padding-left: 0;
    }

    #slider .track > * > *:nth-child(even) .hotel-card {
      margin-left: 16px;
    }

    #slider button.prev {
      left: 16px;
    }

    #slider button.next {
      right: 16px;
    }
  }

  @media (min-width: 960px) {
    #slider button.prev {
      left: calc(50% - 463px);
    }

    #slider button.next {
      right: calc(50% - 463px);
    }
  }

