styles:
.wccp_pro_copy_code_button
{
	line-height: 6px;
	width: auto;
	font-size: 8pt;
	font-family: tahoma;
	margin-top: 1px;
	margin-right: 2px;
	position:absolute;
	top:0;
	right:0;
	border-radius: 4px;
	opacity: 100%;
	margin-top: -30px;
}
.wccp_pro_copy_code_button:hover
{
	opacity: 100%;
}

.wccp_pro_copy_code_button[disabled]
{
	opacity: 40%;
	border-color: red;
}
code,pre
{
	overflow: visible;
	white-space: pre-line;
}

img{
	-moz-user-select: none;
	-webkit-user-select: none;
	-ms-user-select: none;
	-khtml-user-select: none;
	user-select: none;
	-webkit-user-drag: none;
}

.protectionOverlaytext{
	position: absolute;
	width: 100%;
	height: 100%;
	top: 0;
	left: 0;
	display: block;
	z-index: auto;
	-webkit-touch-callout: none;
	-webkit-user-select: none;
	-khtml-user-select: none;
	-moz-user-select: none;
	-ms-user-select: none;
	user-select: none;

	font-weight: bold;
	opacity: 0.0065;
	text-align: center;
	transform: rotate(0deg);
}
.protectionOverlaytext img{
	-webkit-touch-callout: none;
	-webkit-user-select: none;
	-khtml-user-select: none;
	-moz-user-select: none;
	-ms-user-select: none;
	user-select: none;
	pointer-events: none;
}


