$(document).ready(function(){
if ((screen.width > 768)) {
$('#banner').css('display','block');
$('#banner-mobile').css('display','none');
$('#banner .items:gt(0)').fadeOut(0);
setInterval(function(){
$('#banner > :first-child').fadeOut(1000).next('.items').fadeIn(1000).end().appendTo('#banner');}, 5000);
}
else {
$('#banner').css('display','none');
$('#banner-mobile').css('display','block');
$('#banner-mobile .items:gt(0)').fadeOut(0);
setInterval(function(){
$('#banner-mobile > :first-child').fadeOut(1000).next('.items').fadeIn(1000).end().appendTo('#banner-mobile');},5000);
}
});

