      .iti__country {
        margin: 0 !important;
        padding: 5px 10px !important;
      }
      .iti__country-name, .iti__flag-box{
        color: black;
        margin-right: 6px !important;

      }
      .iti__flag-box{
        width: 20px !important;
      }
      .iti__flag{
        height: 15px !important;
        box-shadow: 0 0 1px 0 #888 !important;
        background-image: url('https://plb.ac.id/id/wp-content/plugins/watzap/assets/vendor/intltel/img/flags.png') !important;
        background-repeat: no-repeat !important;
        background-color: #dbdbdb !important;
        background-position: 20px 0 !important;
        width: 20px !important;
      }
      .iti__divider{
        margin-left: 0px !important;
      margin-right: 0px !important;
      margin-top: 0px !important;
      }
      .iti{
        display: block !important;
        position: relative !important;
      }
      .iti--separate-dial-code .iti__selected-dial-code{
        color: black;
        margin-left: 6px !important;
      }
      .iti__flag-container{
        position: absolute !important;
      }
      .iti__selected-flag{
        position: relative !important;
        padding: 0 6px 0 8px !important;
        height: 100% !important;
      }
      .iti__arrow{
        margin-left: 6px !important;
        width: 0 !important;
        height: 0 !important;
        border-left: 3px solid transparent !important;
        border-right: 3px solid transparent !important;
        border-top: 4px solid #555 !important;
      }
      .iti--separate-dial-code .iti__selected-flag{
        background-color: rgba(0,0,0,.05) !important;
      }
      .iti__country-list{
        position: absolute !important;
        z-index: 2 !important;
        list-style: none !important;
        text-align: left !important;
        padding: 0 !important;
        margin: 0 0 0 -1px !important;
        box-shadow: 1px 1px 4px rgb(0 0 0 / 20%) !important;
        background-color: #fff !important;
        border: 1px solid #ccc !important;
        white-space: nowrap !important;
        max-height: 200px !important;
        overflow-y: scroll !important;
        -webkit-overflow-scrolling: touch !important;
      }

      div.wpforms-container-full .wpforms-form ul.iti__country-list{
        position: absolute !important;
        z-index: 2 !important;
        list-style: none !important;
        text-align: left !important;
        padding: 0 !important;
        margin: 0 0 0 -1px !important;
        box-shadow: 1px 1px 4px rgb(0 0 0 / 20%) !important;
        background-color: #fff !important;
        border: 1px solid #ccc !important;
        white-space: nowrap !important;
        max-height: 200px !important;
        overflow-y: scroll !important;
        -webkit-overflow-scrolling: touch !important;
      }

      div.wpforms-container-full .wpforms-form ul.iti__country-list li{
        margin-bottom: 0px !important;
        padding: 5px 10px !important;
      }
      div.wpforms-container-full .wpforms-form ul.iti__country-list li.iti__highlight{
        background-color: rgba(0,0,0,.05) !important;
      }



