.navbar-nav>li>.nav-link::before {
  content: '';
  position: absolute;
  bottom: 20px;
  width: 0;
  left: 8px;
  height: 2px;
  background-color: var(--p-accent-color);;
  opacity: 0;
  -webkit-transition: all .3s ease-in-out;
  -o-transition: all .3s ease-in-out;
  transition: all .3s ease-in-out;
}
.navbar-nav>li>.nav-link:hover::before{
  width: 50%;
  opacity: 1;
}
.active_link_navbar::before{
  opacity:1!important;
  width:50%!important;
}
.tnc_anchor a{

  color:var(--p-txt-darkm-color);
}
.basic_link_color a:hover{
  color: var(--p-link-txt-color);
}
.anchor_brnd_hov:hover{
color:var(--p-accent-color)!important;
}
#post-type-logo {
  color: white;
  position: absolute;
  top: 15px;
  right: 15px;
  height: 2rem;
  width: 2rem;
  background-color: rgba(138, 138, 138, 0.5); 
  border-radius: 50%;
  padding: 2px;
  z-index: 3;

  justify-content: center;
  align-items: center;
}

#post-type-logo div{
  height: 1.2rem;
  width: 1.2rem;
}

#post-type-logo img{
  width: 100%;
  height: 100%;
  border:none;
}
#post-type-logo svg {
  fill: white
}
#gallery-logo {
  height: 1.2rem !important;

}
.category-post-type-logo {
  position: absolute;
  top: 10px;
  right: 10px;
  height: 2rem;
  width: 2rem;
  background-color: rgba(138, 138, 138, 0.5); 
  border-radius: 50%;
  padding: 2px;
  z-index: 3;
  justify-content: center;
  align-items: center;
  color: white;
}

.category-post-type-logoo div {
  height: 1.2rem;
  width: 1.2rem;
}

.category-post-type-logo img{
  width: 100%;
  height: 100%;
  border: none;
}
.category-post-type-logo svg {
  fill: white;
}

.subscribe_body{
  position: relative;
  margin-top: 50px;
  padding: 12px 80px;
  margin-right: 0px !important;
  margin-left: 0px !important;

  border-top-width: 3px !important;
  border-right-width: 3px !important;
  border-bottom-width: 3px !important;
  border-left-width: 3px !important;
  border-left-color: #ea246b  !important;
  border-left-style: dashed !important;
  border-right-color: #ea246b  !important;
  border-right-style: dashed !important;
  border-top-color:#ea246b  !important;
  border-top-style: dashed !important;
  border-bottom-color: #ea246b  !important;
  border-bottom-style: dashed !important;
  border-radius: 3px !important;
  max-width: 100%;
  margin-bottom: 40px;
  text-align:center;
}
.subscribe_body h2{
  font-size: 32px;
  text-align: center;
  font-weight:700;
  font-style:normal;
  margin-top: 55px;
    margin-bottom: 40px;
    color:var(--p-link-txt-color);
}
.subscribe_body .image_figure{
  position: absolute;
    top: -17px;
    left: 21px;
    width: 180px;
    background: var(--p-bg-color);
    padding-left: 25px;
}
.subscribe_body .emoji{
  display: inline !important;
  border: none !important;
  box-shadow: none !important;
  height: 1em !important;
  width: 1em !important;
  margin: 0 0.07em !important;
  vertical-align: -0.1em !important;
  background: none !important;
  padding: 0 !important;
}
.btn-sub-block {
  font-size: 14px;
  font-weight: normal;
  text-transform: uppercase;
  background: var(--p-accent-color);
  color: #fff;
  padding: 10px;
  border-radius: 5px;
}
.subscribe_body .subscribe_btn{
  margin-top:20px;
  margin-bottom:40px;
}
.sub_text_column p{
  font-size:11.5px;
  margin-left:10px;
}

.uitls_container{
  display:flex;
  align-items:center;
  justify-content: space-between;
  height: 36px;

}
.utility_nav{
  height:36px;
  background-color:var(--p-darkgrey-bg-color);
  width: 100%;
  z-index: 61;
}
.left_side{
  display: flex;
  align-items: center;

}
.switch input {
  opacity: 0;
    width: 0;
    height: 0;
}
.switch {
  position: relative;
  display: inline-block;
  width: 45px;
  height: 25px;
}
.nav_switch{
  display:none;
}
.slider {
  position: absolute;
  cursor: pointer;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: #646464;
  -webkit-transition: .4s;
  transition: .4s;
}

.slider:before {
    color: #666;
    background-color: #fff;
    font-size: 14px!important;
    position: absolute;
    content: "\263C";
    height: 19px;
    line-height: 20px;
    width: 19px;
    text-align: center;
    left: 3px;
    bottom: 3px;
    box-shadow: 0 1px 3px rgba(0,0,0,.3);
    transition: .25s;
}
input:checked + .slider {
  background-color:#646464;
}

input:focus + .slider {
  box-shadow: 0 0 1px #2196F3;
}

input:checked + .slider:before {
  content:"\263E";
  -webkit-transform: translateX(20px);
  -ms-transform: translateX(20px);
  transform: translateX(20px);
}

/* Rounded sliders */
.slider.round {
  border-radius: 25px;
}

.slider.round:before {
  border-radius: 50%;
}
