}
  .right_feat{
    display:none;
  }
  .feat-a-1-title{
    font-size:16px;
    line-height: 1.3em;
  }

  .pad>*{
    padding-right: calc(var(--bs-gutter-x) * .5);
    padding-left: calc(var(--bs-gutter-x) * .5);
  }
  .col-md-4 {
    flex: 0 0 auto;
    width: 100%;
}
.article_content h2 {
  font-size:14px;
  }
  .tinymce-table-div{
    max-width: 300px;
  }
}
@media only screen and (min-width: 550px) {
  .signin_main_container{border-radius: 10px;background: var(--p-dark-bg-border-shadow);box-shadow: 0px 1px 30px 0px rgba(0, 0, 0, 0.06);margin-top: 5%;padding-top: 30px;}
  .back-link{display: block;}
  .sign_up_form_below_hr{display: block;}
  .sign_up_form_below_text{flex-direction: column-reverse;}
  .singup_tnc_position{margin-top: 15px !important;}
  .sign-in-header {margin-top: 0px;}

}


.display-none{
  display: none;
}
.display-block{
