function checkPOUpdate(){
	console.log($(".orderConfirmation-section strong").length);
	if($(".orderConfirmation-section strong").length == 0){
		setTimeout(checkPOUpdate, 100);
	}else{
		console.log("found!" + $(".orderConfirmation-section strong").text());
		let orderID = $(".orderConfirmation-section strong").text();
		const storehash = 'j599icwg8d';
		const poValue = localStorage.getItem('checkout-po-input');
		if (!poValue || poValue.length === 0) throw new Error('No PO Input value found');
		$.get(`/api/storefront/orders/${orderID}` , function(data) {
			const existingOrderComments = data.customerMessage;
			// check if the po input was already added to the order
			if (existingOrderComments.indexOf(`PO Number: ${poValue}`) > -1) throw new Error('PO Input already added');
			const newOrderComments = `PO Number: ${poValue}\nOrder Comments: ${existingOrderComments}`;
			$.ajax({
				url: `https://www.ribon.ca/api/live/${storehash}/put`,
				type: 'post',
				dataType: 'json',
				contentType: 'application/json; charset=utf-8',
				data: JSON.stringify({ uri: `/v2/orders/${orderID}`, query: { customer_message: newOrderComments }}),
				success: function() {
					localStorage.removeItem('checkout-po-input');
					console.log('Order updated successfully!');
				},
				error: function(e) {
					localStorage.removeItem('checkout-po-input');
					throw new Error('Failed to update order', e);
				}
			});
		}).fail(function() {
			localStorage.removeItem('checkout-po-input');
			throw new Error('Failed to retrieve order');
		});
	}
}
checkPOUpdate();

// this only runs on checkout + order confirmation (no script manager option for both)
if (window.location.href.indexOf('/checkout') === -1) throw new Error('Skipping PO Input Script');

$(window).on('load', function (e) {
	const poConfig = {
		gatewaysToValidate: ['instore'],
		validationMessage: 'A PO Number must be provided',
		storageKey: 'checkout-po-input',
		containerClass: 'po-input-container',
		inputId: 'po-number-input',
		inputMaxLength: 25,
		inputPlaceHolder: 'PO Number::'
	};
    const $poInput = $(`<div class="${poConfig.containerClass}">
		<div>
			<legend class="form-legend optimizedCheckout-headingSecondary">${poConfig.inputPlaceHolder}</legend>
			<input maxlength="${poConfig.inputMaxLength}" aria-invalid="false" aria-required="false" type="text" id="${poConfig.inputId}" class="form-input optimizedCheckout-form-input">
		</div>
	</div>`);

    function initPoInput() {
        setInterval(function() {
            if ($(".checkout-step.checkout-step--payment > .checkout-view-content:visible").length > 0) {
                const $paymentSection = $('.checkout-step.optimizedCheckout-checkoutStep.checkout-step--payment');
                if ($paymentSection.find('.checkout-form').length > 0 && $paymentSection.find(`.${poConfig.containerClass}`).length === 0) {
                    $paymentSection.find('.checkout-form').prepend($poInput);
					const poValue = window.localStorage.getItem(poConfig.storageKey);
					if (poValue && poValue.length > 0) $poInput.find(`#${poConfig.inputId}`).val(poValue);
                    $poInput.find(`#${poConfig.inputId}`).on('input', onPoInput);
                }
            }
            if ($('#checkout-payment-continue').length > 0) {
				$('#checkout-payment-continue').off('click');
            	$('#checkout-payment-continue').click(validatePoInput);
            }
        }, 500);
    }

    const validatePoInput = function(e) {
		const gatewayId = getGatewayId();
		if (poConfig.gatewaysToValidate.indexOf(gatewayId) === -1) return;
		const poInputValue = $(`#${poConfig.inputId}`).val();
		const poStorageValue = window.localStorage.getItem(poConfig.storageKey);
		if (!poInputValue || poInputValue.length === 0 ||
			!poStorageValue || poStorageValue.length === 0) {
			e.preventDefault();
			alert(poConfig.validationMessage);
		}
	}

	const getGatewayId = function() {
		let id = $('.optimizedCheckout-form-checklist-item:not(.mbc-hidden) .optimizedCheckout-form-checklist-checkbox:checked[name=paymentProviderRadio]').attr('id');
		if (!id) id = $('.optimizedCheckout-form-checklist-item .optimizedCheckout-form-checklist-checkbox:checked[name=mbcPaymentProviderRadio]').attr('id');
		if (id) {
			id = id.replace('radio-', '');
			return id;
		}
		return '';
	};

    function onPoInput() {
		const value = $(`#${poConfig.inputId}`).val();
		window.localStorage.setItem(poConfig.storageKey, value);
	}

    function savePoToOrderComments() {
        const poValue = window.localStorage.getItem(poConfig.storageKey);
        if (poValue || poValue.length === 0) return;

    	// moved to Affiliate Conversion Tracking script
    }

    if (window.location.pathname === '/checkout') {
    	initPoInput();
    }

    if (window.location.pathname === '/checkout/order-confirmation') {
    	savePoToOrderComments();
    }
});

