window.addEventListener('scroll', function() {
	var header = document.querySelector('.header');
	var scrollPosition = window.scrollY;

	// Calculate the opacity based on scroll position
	var opacity = scrollPosition > 100 ? 1 : scrollPosition / 100;

	// Set the background color with the calculated opacity
	header.style.transition = 'opacity 15s';
	header.style.backgroundColor = 'url(../site_images/hero_slightblue.svg) bottom no-repeat #2e61bc';
	header.style.backgroundImage = 'url(../site_images/hero_slightblue.svg) bottom no-repeat #2e61bc';
});
