    async function fetchPage(url) {
        const response = await fetch(url);
        if (!response.ok) {
            throw new Error(`Ошибка: ${response.status}`);
        }
        return response.text();
    }

    function parseHTML(html) {
    const parser = new DOMParser();
    const doc = parser.parseFromString(html, 'text/html');
    const newsItems = doc.querySelectorAll('.list__item');
    const newsList = [];
	newsItems.forEach(item => {

    if (item.classList.contains('list__item--main')) {
        return; 
    }
        const titleElement = item.querySelector('.list__title a');
        const dateElement = item.querySelector('.list__date.meta__item span');

        if (titleElement) {
            const title = titleElement.innerText;
            const relativeLink = titleElement.getAttribute('href');
            const link = relativeLink ? 
                (relativeLink.startsWith('http') ? relativeLink : `https://digital.midural.ru${relativeLink}`) 
                : null;
            const date = dateElement ? dateElement.innerText : 'Дата не указана';

            newsList.push({ title, link, date });
        }
    });

    return newsList;
}


    async function scrapeNews() {
        const url = 'https://digital.midural.ru/presscenter/news/';
        try {
            const html = await fetchPage(url);
            const news = parseHTML(html);
            insertNewsIntoContainer(news);
        } catch (error) {
            console.error('Ошибка при получении новостей:', error);
        }
    }

    function insertNewsIntoContainer(news) {
        const newsContainer = document.querySelector('#news-container .collection_item');
        newsContainer.innerHTML = '';
        const latestNews = news.slice(0, 3);
        latestNews.forEach(item => {
            const listItem = document.createElement('li');
            listItem.innerHTML = `<li><a href="${item.link}" rel='nofollow'> <span class='Title'> ${item.title}</span><span class='date nobreak byline_date'>${item.date}</span></a></li>`;
            newsContainer.appendChild(listItem);
        });
    }

    document.addEventListener("DOMContentLoaded", scrapeNews); // Запускаем функцию после загрузки DOM
