// Duration in milliseconds to wait while the Kameleoon application file is loaded
var kameleoonLoadingTimeout = 1000;

window.kameleoonQueue = window.kameleoonQueue || [];
window.kameleoonStartLoadTime = new Date().getTime();
if (! document.getElementById("kameleoonLoadingStyleSheet") && ! window.kameleoonDisplayPageTimeOut) {
    var kameleoonS = document.getElementsByTagName("script")[0];
    var kameleoonCc = "* { visibility: hidden !important; background-image: none !important; }";
    var kameleoonStn = document.createElement("style");
    kameleoonStn.type = "text/css";
    kameleoonStn.id = "kameleoonLoadingStyleSheet";
    if (kameleoonStn.styleSheet) {
        kameleoonStn.styleSheet.cssText = kameleoonCc;
    }
    else {
        kameleoonStn.appendChild(document.createTextNode(kameleoonCc));
    }
    kameleoonS.parentNode.insertBefore(kameleoonStn, kameleoonS);
    window.kameleoonDisplayPage = function(fromEngine) {
        if (!fromEngine) {
            window.kameleoonTimeout = true;
        }
        if (kameleoonStn.parentNode) {
            kameleoonStn.parentNode.removeChild(kameleoonStn);
        }
    };
    window.kameleoonDisplayPageTimeOut = window.setTimeout(window.kameleoonDisplayPage, kameleoonLoadingTimeout);
}

let totalErrorsOnPage = 10;
let currentErrorsSent = 0;
const loggingApiUrl = 'https://shop-api-origin.parfumdreams.de:443/api/FR' + '/Diagnostic/FrontendErrorJavascript';

let getCookieValue = function (c_name) {
    var c_value = document.cookie;
    var c_start = c_value.indexOf(" " + c_name + "=");

    if (c_start === -1) {
        c_start = c_value.indexOf(c_name + "=");
    }

    if (c_start === -1) {
        c_value = null;
    } else {
        c_start = c_value.indexOf("=", c_start) + 1;
        var c_end = c_value.indexOf(";", c_start);
        if (c_end === -1) {
            c_end = c_value.length;
        }
        c_value = c_value.substring(c_start, c_end);
    }
    return c_value;
}

const global_error_options = function () {
    return {
        url: window.location.href,
        referrer: document.referrer,
        userAgent: window.navigator.userAgent,
        screenWidth: window.innerWidth,
        clientDeviceId: getCookieValue("ClientDeviceId"),
        clientSessionId: getCookieValue("ClientSessionId")
    }
}


let oldconsoleerror = window.console.error.bind(window.console)
window.console.error = function (...args) {
    oldconsoleerror.apply(null, args);

    currentErrorsSent = currentErrorsSent + 1;
    if (currentErrorsSent > totalErrorsOnPage) {
        return;
    }

    let completeError = args[0];

    try {

        if (args[0] && typeof args[0] === 'string') {
            let toAdd = args[0].toString();
            if (toAdd.indexOf("DONT_SEND_TO_BACKEND_FOR_LOGGING") > -1) {
                return; 
            }
            completeError = completeError + toAdd;
        }
        if (args.length > 1 && typeof args[1].url === 'string') {

            completeError = completeError + args[1].url;
        }

        if (args.length > 1 && (typeof args[1].status === 'number' || typeof args[1].status === 'string')) {

            completeError = completeError + " status: " + args[1].status.toString();
        }

        if (args.length > 1 && (typeof args[1].message === 'string')) {

            completeError = completeError + " msg: " + args[1].message.toString();
        }
        if (args.length > 1 && (typeof args[1].stack === 'string')) {

            completeError = completeError + " stack: " + args[1].stack.toString();
        }
    } catch (ex) {
        completeError = completeError + "ERROR in ERROR HANDLER";
    }

    let partitionKey = typeof completeError === 'string' ? completeError.substring(0, 50) : 'empty-partition-key';

    function ConvertMessageToString(message) {
        if (typeof message === 'string') {
            return message;
        } else { 
            try {
                return JSON.stringify(message);
            } catch (e) {
                return "problem parsing message object on " + window.location.href;
            }
        }

    }

    try {
        const logginginfo = Object.assign({},
            global_error_options(),
            {
                logSource: 'overwrittenconsole',
                msg: ConvertMessageToString(completeError)
            });
        fetch(loggingApiUrl, { mode: 'no-cors', method: 'POST', body: JSON.stringify(logginginfo) });

    } catch (ex) {

    }
}

window.addEventListener('error', function (message) {

    currentErrorsSent = currentErrorsSent + 1;
    if (currentErrorsSent > totalErrorsOnPage) {
        return;
    }

    let errorToSend = "empty error to send";
    try {
        errorToSend = typeof message == 'string' ? message : "OnErrorGlobalEvent: " + message.message;
    } catch (e) {
        errorToSend = 'error inside the error handler!';
    }
    let partitionKey = typeof errorToSend === 'string' ? errorToSend.substring(0, 50) : 'empty-partition-key';

    try {
        const logginginfo = Object.assign({},
            global_error_options(),
            {
                logSource: 'globalerrorhandler',
                msg: errorToSend
            });
        fetch(loggingApiUrl, { mode: 'no-cors', method: 'POST', body: JSON.stringify(logginginfo) });

    } catch (ex) {

    }
})

