var slug = 'home';
var expander = '<img src="/images/nav_expander.svg" height="16" class="pl2">';//' [+]';
var hider = '<img src="/images/nav_hider.svg" height="16" class="pl2">';//' [-]';

function setHiddenState(navgroup, hidden, ele, tabcounter) {
  if(hidden) {
    navgroup.hide();
    ele.html(ele.attr('title') + expander);
  } else {
    ele.html(ele.attr('title') + hider);
  }
  ele.attr('tabindex', tabcounter);
}

$(function() {
  $("a.inactive").click(function() {return false;});
  var tabindexcounter = 0;
  $(".navigation-control").each(function() {
    var hide = true;
    var ele = $(this);
    var navgroup = $('#' + ele.attr('section'));
    ele.html(ele.attr('title') + expander);
    slug = ('analysis2' == slug) ? 'analysis' : slug;
    $(navgroup.children('a').each(function(){
      if(
        (new RegExp('test')).test(this.href)
        ||
        (new RegExp('reading')).test(this.href)
        ||
        (new RegExp(slug)).test(this.href)
      ) {
        hide = false;
      }
    }));
    always_expand = ['political_compass', 'whimseys', 'au', 'ca']
    if( always_expand.includes( ele.attr('section'))) {
      hide = false;
    }
    setHiddenState(navgroup, hide, ele, ++tabindexcounter);
  });
  $(".navigation-control").on('click keyup', function() {
    var ele = $(this);
    var navgroup = $('#' + ele.attr('section'));
    if(navgroup.is(":visible")) {
      navgroup.slideUp(400, function() {
        ele.html(ele.attr('title') + expander);
      });
    } else {
      navgroup.slideDown(400, function() {
        ele.html(ele.attr('title') + hider);
      });
    }
  });
  $('a').each(function() {
    $(this).attr('tabindex', ++tabindexcounter);
  });
});

