scripts:
window.dataLayer = window.dataLayer || [];
function gtag(){dataLayer.push(arguments);}
gtag('js', new Date());

gtag('config', 'G-KZNHLVTLZE');

function showTab(index, type='') {
	let $tabs = $(`.${type=="brand" ? 'brand-':''}tabs li`);
	let $contents = $(`.${type=="brand" ? 'brand-':''}tab-content`);

	$tabs.removeClass('active');
	$contents.addClass('d-none');

	$tabs.eq(index).addClass('active');
	$contents.eq(index).removeClass('d-none');
}

