      videoContainer.innerHTML = `<iframe class="lr-container__video--content" style="" src="${videoURL}" frameborder="0" allow="autoplay; fullscreen" allowfullscreen></iframe>`;
      loadScript('https://player.vimeo.com/api/player.js', function(){
        var player = new Vimeo.Player(videoContainer);
        window.player = player

        player.on('timeupdate', function(e) {
          // console.log({e, wpb: window.playBackPercent})
          if(!window.playBackPercent && e.percent > 0.25) {
            window.playBackPercent = 0.25
            trackVideoEvent('video_25', videoURL)
          } else if(window.playBackPercent < 0.50 && e.percent > 0.50) {
            window.playBackPercent = 0.50
            trackVideoEvent('video_50', videoURL)
          } else if(window.playBackPercent < 0.75 && e.percent > 0.75) {
            window.playBackPercent = 0.75
            trackVideoEvent('video_75', videoURL)
          } else if(window.playBackPercent < 1 && e.percent > 0.95) {
            window.playBackPercent = 1
            trackVideoEvent('video_end', videoURL)
          }
        });
      })
  }
