  function preloadImage(url) {
    return new Promise(function(resolve, reject) {
      var image = new Image();
      image.onload = resolve;
      image.onerror = reject;
      image.src = url;
    });
  }

  async function changeBackground(imageUrl) {
    var heroImage = document.querySelector('.lr-home-hero');

    try {
      await preloadImage(imageUrl);
      heroImage.style.backgroundImage = "linear-gradient(0deg, rgba(0, 0, 0, 0.4), rgba(0, 0, 0, 0.4)), url('" +
        imageUrl + "')";
      heroImage.style.backgroundPosition = "center";
      heroImage.style.backgroundSize = "cover";
      heroImage.style.backgroundRepeat = "no-repeat";
    } catch (error) {
      console.error("Error loading image:", error);
    }
  }

