        }

        initValues();
        KT_countdown_update = window.setInterval(() => {
          update_KT_CT();
        }, 1000);
        jQuery('.countdown-KT-full-width').removeClass('hide-KT');
        jQuery('.countdown-KT-full-width').addClass('fade-in-KT');
      }

      start_Countdown_KT();
    }
