.appikon-bis-inline-form-wrapper {
    border: 1px solid #ccc;
    padding: 15px;
    margin: 15px auto;
    border-radius: 2px;
    max-width: 600px;
    overflow: auto;
}
.appikon-bis-inline-form-wrapper .appikon-bis-inline-form-title {
    overflow: hidden;
    margin: 0px;
    padding: 0px;
    margin-bottom: 15px;
}
.appikon-bis-inline-form-wrapper .appikon-bis-inline-form-title h3 {
    text-transform: none;
    margin: 0px;
    padding: 0px;
}
.appikon-bis-inline-form-wrapper .appikon-bis-inline-form-description {
    margin: 0px;
    padding: 0px;
    margin-bottom: 10px;
    line-height: 1.4em;
}
.appikon-bis-inline-form-wrapper .appikon-bis-inline-form-submit {
    width: 100%;
    margin: 0px;
    margin-top: 10px;
    text-align: center;
}
.appikon-bis-inline-form-wrapper #appikon-bis-inline-form-message {
    margin: 0px;
    padding: 0px;
    margin-top: 10px;
    text-align: center;
}
.appikon-bis-inline-form-wrapper #appikon-bis-inline-form-message > p {
    margin: 0px;
    padding: 6px 11px;
    border: 1px solid transparent;
    border-radius: 2px;
}
.appikon-bis-inline-form-success {
    background-color: #dff0d8;
    border-color: #dff0d8;
    color: #3c763d;
}
.appikon-bis-inline-form-error {
    border-color: #f2dede;
    background-color: #f2dede;
    color: #a94442;
}
.appikon-bis-inline-form-wrapper .appikon-bis-inline-form-footer {
    margin-top: 10px;
    font-size: small;
}
.appikon-bis-inline-form-wrapper .appikon-bis-inline-form-powered-by {
    padding: 0px;
    margin-top: 10px;
    text-align: center;
    opacity: .8;
}
.appikon-bis-inline-form-wrapper .appikon-bis-inline-form-powered-by hr {
    margin: 10px 0;
    border: 0;
    border-bottom: 1px solid #ccc;
}
.appikon-bis-form-hide-warning{
    display:none
}
.appikon-bis-form-warning{
     color:red
}
