            if (done === true) {
                ajaxTimelineLoad($(this));
            }
            return false;
        });
    });

    // Funktion zum laden des Contents
    function ajaxTimelineLoad(btn) {
        done = false;
        var url = btn.prop('href');
        btn.html('<i class="fa fa-spinner fa-pulse fa-fw fa-2x"></i>');

        // AJAX Call
        $.ajax({
            url: url,
            type: 'GET',
            dataType: 'html',
            success: function (data) {
                $('.timeline-loading-button').remove();
                $(data).insertBefore($('#end-of-timeline'));
                done = true;
            },
            error: function (data) {
                btn.html('<i class="fa fa-warning fa-2x"></i>');
                done = true;
            }
        });
    }


