if(typeof Geolizr === "undefined") {
    var Geolizr = {};
    Geolizr.currencyDynamicCountry = false;
    Geolizr.version = 20200327;
    Geolizr.waitforMoneyFormat = true;
}

// save geolizr-lib.js url to the sessionStorage
sessionStorage.setItem('geolizr_lib_url', "//www.gitticonsciousbeauty.com/cdn/shop/t/479/assets/geolizr-lib.js?v=52600507088314058931718912797");
if(typeof Geolizr.events === "undefined") Geolizr.events = {};
if(typeof Geolizr.geolizrEvents === "undefined") Geolizr.geolizrEvents = [];

Geolizr.config = function(config) {
    Geolizr.currency_widget_enabled = config.currency_widget_enabled === "true" || false;
    Geolizr.shopCurrency = 'EUR';
    Geolizr.shopifyFormatMoneySet = false;
    Geolizr.observeElements = [];
};




Geolizr.moneyFormats = {};


Geolizr.shopifyCurrencyList = [];
Geolizr.shopifyCurrency = {};
Geolizr.shopifyCurrencyListIndex = {};
let setShopifyCurrencyToGeolizr = function() {
    let GeolizrEachCurrency = {};

    GeolizrEachCurrency = {
        "iso_code": "CHF",
        "name": "Swiss Franc",
        "symbol": "CHF",
        "shop_currency": false
    };
    if(GeolizrEachCurrency.shop_currency) {
        Geolizr.shopifyCurrency = GeolizrEachCurrency;
    }
    Geolizr.shopifyCurrencyList.push(GeolizrEachCurrency);
    Geolizr.shopifyCurrencyListIndex[GeolizrEachCurrency.iso_code] = Geolizr.shopifyCurrencyList.indexOf(GeolizrEachCurrency);

    GeolizrEachCurrency = {
        "iso_code": "DKK",
        "name": "Danish Krone",
        "symbol": "kr.",
        "shop_currency": false
    };
    if(GeolizrEachCurrency.shop_currency) {
        Geolizr.shopifyCurrency = GeolizrEachCurrency;
    }
    Geolizr.shopifyCurrencyList.push(GeolizrEachCurrency);
    Geolizr.shopifyCurrencyListIndex[GeolizrEachCurrency.iso_code] = Geolizr.shopifyCurrencyList.indexOf(GeolizrEachCurrency);

    GeolizrEachCurrency = {
        "iso_code": "EUR",
        "name": "Euro",
        "symbol": "€",
        "shop_currency": true
    };
    if(GeolizrEachCurrency.shop_currency) {
        Geolizr.shopifyCurrency = GeolizrEachCurrency;
    }
    Geolizr.shopifyCurrencyList.push(GeolizrEachCurrency);
    Geolizr.shopifyCurrencyListIndex[GeolizrEachCurrency.iso_code] = Geolizr.shopifyCurrencyList.indexOf(GeolizrEachCurrency);

}

setShopifyCurrencyToGeolizr();
/* Geolizr Provider Setter */
Geolizr.presetService = false;
Geolizr.activateIpInfo = function(accessKey) { Geolizr.presetService = { name: 'ipInfo', accessKey: accessKey }; };
Geolizr.activateLovelyAppProvider = function() { Geolizr.presetService = { name: 'lovelyAppProvider' }; };
Geolizr.activateIpApiCo = function(accessKey) { Geolizr.presetService = { name: 'ipApiCo', accessKey: accessKey }; };
Geolizr.activateDbIpProvider = function(accessKey) { Geolizr.presetService = { name: 'dbIpProvider', accessKey: accessKey }; };
Geolizr.activateIpStackProvider = function(accessKey) { Geolizr.presetService = { name: 'ipStackProvider', accessKey: accessKey }; };
Geolizr.activateGeolizrIPApiProvider = function(accessKey) { Geolizr.presetService = { name: 'geolizrIpApiProvider', accessKey: accessKey }; };



/* Service Aliase */
Geolizr.activateFreegeoip = Geolizr.activateIpStackProvider;
Geolizr.requestLocaleRootUrl = "\/en";
Geolizr.addSystemEventListener = function(name, callback) {
    if (typeof Geolizr.realInit !== 'undefined') {
        Geolizr.realInit(function($) {
            callback({jquery: $});
        })
    } else {
        Geolizr.geolizrEvents.push(new CustomEvent(name));
        document.addEventListener(name, callback);
    }
};

Geolizr.addEventListener = function(name, callback, priority) {
    if(typeof priority == "undefined") priority = 100;
    if(typeof Geolizr.realAddEventListener === "undefined") {
        if (!Geolizr.events[name]) Geolizr.events[name] = [];
        Geolizr.events[name].push({callback: callback, priority: priority});
    } else {
        Geolizr.realAddEventListener(name, callback, priority);
    }
};

Geolizr.getGeoData = function(callback) {
    if(typeof Geolizr.realGetGeoData === "undefined") {
        Geolizr.addEventListener('geolizr.init', function() {
            Geolizr.getGeoData(callback);
        }, 200)
    } else {
        Geolizr.realGetGeoData(callback);
    }
};

Geolizr.init = function(callback, priority) {
    if(typeof Geolizr.realInit === "undefined")
        Geolizr.addEventListener("geolizr.init", callback, priority);
    else
        Geolizr.realInit(callback, priority);
};


Geolizr.notification_widget_enabled = false;
Geolizr.popup_widget_enabled = false;
Geolizr.api_widget_enabled = false;
Geolizr.currency_widget_enabled = false;
Geolizr.redirect_widget_enabled = false;

Geolizr.enables = {
    'notification_widget_enabled': Geolizr.notification_widget_enabled,
    'popup_widget_enabled': Geolizr.popup_widget_enabled,
    'api_widget_enabled': Geolizr.api_widget_enabled,
    'currency_widget_enabled': Geolizr.currency_widget_enabled,
    'redirect_widget_enabled': Geolizr.redirect_widget_enabled
};

if(Geolizr.popup_widget_enabled || Geolizr.notification_widget_enabled || Geolizr.currency_widget_enabled) {

    Geolizr.geolizrCssFile = "//www.gitticonsciousbeauty.com/cdn/shop/t/479/assets/geolizr-lib.css?v=59017386019965299001718912798";

}

