sessionStorage.removeItem('BC::Cart-Error');

class CleverCartErrors extends HTMLElement {


    constructor() {
        super()
    }

    connectedCallback() {
        window.addEventListener('cart:error', (e) => {
            console.log("cart error",e)
            this.innerHTML = `<p>${e.detail.errors || e.detail.message}</p>`
        })
        window.addEventListener('cart:error:clear', (e) => {
            console.log("cart error clear",e)
            this.innerHTML = ""
        })



    }

}

customElements.define('clever-cart-errors', CleverCartErrors);

