let lastScrollTop = 0;
window.addEventListener("scroll", function(){
    let st = window.pageYOffset || document.documentElement.scrollTop;

    if (st > lastScrollTop ){
        document.querySelector('header').classList.remove('fixed');
        document.querySelector('#announcement_bar').classList.remove('fixed-announcement');
    }
    else {
        if (window.scrollY > 50) {
            document.querySelector('header').classList.add('fixed');
            document.querySelector('header').classList.remove('announcement-padding');
            document.querySelector('#announcement_bar').classList.add('fixed-announcement');
        }
    }
    if (window.scrollY < 50) {
            let header = document.querySelector('header')
            if(!!header){
                header.classList.remove('fixed');
                let announcement = document.querySelector('#announcement_bar');
                if(!!announcement) {
                    if (announcement.classList.contains('initial-announcement')) {
                        header.classList.add('announcement-padding');
                    } else {
                        header.classList.remove('announcement-padding');
                    }
                    announcement.classList.remove('fixed-announcement');
                }
            }
    }
    lastScrollTop = st <= 0 ? 0 : st; /* For Mobile or negative scrolling */
})

function klaviyoBIScollection(class_btn, name_product, id_product, id_variant){
  const bis_frame = document.getElementById('klaviyo-bis-iframe-collection');
  if(bis_frame!=undefined){
    bis_frame.style.display = "block";
    bis_frame.querySelector('.modal-title').innerHTML = name_product;
    bis_frame.querySelector('.id_product').value = id_product;
    bis_frame.querySelector('.id_variant').value = id_variant;
    bis_frame.addEventListener('click', (event) => {
      if (event.target === bis_frame) {
        bis_frame.style.display = 'none';
      }
    });
    bis_frame.querySelector('.close.klaviyo-bis-close').addEventListener('click', (event) => {
      bis_frame.style.display = 'none';
    });

    let bisForm = document.querySelector('#klaviyo-bis-iframe-collection .bis-form');
    if(bisForm != undefined) {
      let email = bisForm.querySelector('input[type="email"]');
      if(email != undefined) {
        email.value = '';
        bisForm.classList.remove('invalid');
        bisForm.classList.remove('error');
        bisForm.classList.remove('success');
        let submit = e => {
          if (email.value != '' && email.checkValidity()) {
            let fd = new FormData()
            fd.append('a', 'VWE5RB')
            fd.append('email', email.value)
            fd.append('variant', id_variant)
            fd.append('platform', 'shopify')
            fetch(`https://a.klaviyo.com/api/v1/catalog/subscribe`,{
              method: 'POST',
              mode: 'no-cors',
              body: fd
            })
            .then(response => {
              bisForm.classList.remove('invalid');
              bisForm.classList.remove('error');
              bisForm.classList.add('success');
            })
            .catch(err => {
              bisForm.classList.remove('invalid');
              bisForm.classList.remove('success');
              bisForm.classList.add('error');
            })
          } else {
            bisForm.classList.remove('success');
            bisForm.classList.remove('error');
            bisForm.classList.add('invalid');
          }
        };
        bisForm.querySelector('[klaviyo-bis-submit]').addEventListener('click', function(event){
          event.preventDefault(); 
          submit();
        });
        email.addEventListener('keydown', e => {
            bisForm.classList.remove('invalid');
            if (e.key === 'Enter') {
                submit();
            }
        });
      }
    }

  }
}

