    display:block;
    overflow: hidden;
    position: relative;
}
cs-nav,
cs-track,
cs-slide,
cs-nav-item{
    display:block;
}
cs-slide{
    position: relative;
}
cs-nav-item{
    cursor:pointer;
    width:12px;
    height:12px;
    border-radius:50%;
