    background-color: transparent;
}
cs-nav-item.in-view{
    background-color: #000000;
}
cs-nav{
    display: flex;
    padding:10px;
    gap:5px;
    justify-content: center;
}
cs-nav.left{
    justify-content: flex-start;
}
cs-nav:empty{
    display:none;
}
cs-nav.right{
    justify-content: flex-end;
}
cs-nav.overlayed{
    position: absolute;
    bottom:0;
    left:0;
    width:100%;
}
clever-slider[fade] cs-slides{
    transform: none !important;
}
clever-slider[fade] cs-slide{
    opacity:0;
    pointer-events: none;
    left: 0 !important;
    right: 0 !important;
}
clever-slider[fade] cs-slide.in-view{
    opacity:1;
    pointer-events: initial;
}
cs-nav-item:only-child{
    display: none;
}
cs-next,
cs-prev{
    display:block;
    cursor:pointer;
    z-index:2;
}
cs-next[disabled],
cs-prev[disabled]{
   opacity: 0.5;
}
cs-next.cs_next,
cs-prev.cs_prev{
    position: absolute;
    top:50%;
    transform: translateY(-50%);
    padding:20px;
}
cs-prev.cs_prev{
    left:0;
}
cs-next.cs_next{
    right:0;
}

