.swatch {
    width:100%;
    font-size:0;
    position: relative;
    border:5px solid white;
    border-radius:50%;
}
.swatch::before{
    display:block;
    content:'';
    padding-bottom:100%;
}
input.swatch {
    display:none;
}
input.swatch:checked + .swatch::after{

content: '';
border: 1px solid #333333;
width: calc(100% + 10px);
height:calc(100% + 10px);
position: absolute;
margin:-5px;
left:0;
top:0;
border-radius:50%;

}
@media (hover: hover) {
    .swatch:hover::after{

