.start-page {
			position: fixed;
			z-index: 9999;
			width: 100%;
			height: 100%;
			background: -webkit-linear-gradient(top, #ff8988 0%, #ffd0ba 100%);
			background: linear-gradient(180deg, #ff8988 0%, #ffd0ba 100%);
			bottom: 0;
			display: -webkit-box;
			display: -webkit-flex;
			display: flex;
			-webkit-box-orient: vertical;
			-webkit-box-direction: normal;
			-webkit-flex-direction: column;
			flex-direction: column;
			-webkit-box-pack: center;
			-webkit-justify-content: center;
			justify-content: center
		}

		.start-page-width {
			max-width: 400px;
		}

		.start-page.daman {
			background: -webkit-linear-gradient(281deg, #E22F2F 1.56%, #FFD0BA 39.74%, #FF8988 39.75%, #FFF 80.92%, #E33333 99%);
			background: linear-gradient(169deg, #E22F2F 1.56%, #FFD0BA 39.74%, #FF8988 39.75%, #FFF 80.92%, #E33333 99%)
		}

		.start-page div {
			display: -webkit-box;
			display: -webkit-flex;
			display: flex;
			-webkit-box-orient: vertical;
			-webkit-box-direction: normal;
			-webkit-flex-direction: column;
			flex-direction: column;
			-webkit-box-pack: center;
			-webkit-justify-content: center;
			justify-content: center;
			-webkit-box-align: center;
			-webkit-align-items: center;
			align-items: center
		}

		.start-page div .dice {
			width: 2.66667rem;
			height: 2.66667rem;
			/* background-image: url(/static/manifest/game_dice.png); */
			background-repeat: no-repeat;
			background-size: contain;
			background-position: center
		}

		.splash-hidden {
			display: none;
		}

		.start-page div .dice.daman {
			width: 24rem;
			height: 21rem;
			/* background-size: cover; */
			background-image: url(/static/manifest/game_66lottery.png)
		}

		.start-page .kfimg {
			display: block;
			position: absolute;
			right: 15px;
			bottom: 15px;
			width: 64px;
			height: 64px;
			/* background-size: cover; */
			background-image: url(/static/common/india_icon_sevice_new.png)
		}

		.start-page div .logo {
			background-repeat: no-repeat;
			background-size: contain;
			margin-top: 2.13333rem;
			max-width: 50%
		}

		.start-page div .logo.daman {
			width: 13rem;
			height: 7rem;
			/* background-size: cover; */
			background-image: url(/static/manifest/game_66lottery_logo.png)
		}

		.start-page div p {
			font-weight: 700;
			font-size: 1.3rem;
			margin-top: .4rem;
			margin-bottom: 5rem;
			padding: 0 .13333rem;
			text-align: center
		}

		@media screen and (max-width: 960px) {
			.start-page-width {
				max-width: 1080px;
			}
		}
