var liMarg = 70,
    isMob = 0;

function positionRectangle(){
    var left = $('.srv-nav ul li.active').offset().left - $('.srv-nav ul').offset().left;
    $('.srv-nav li.line').css('left', left);
}      

$(document).ready(function(){
    $('.our-services-section .srv-nav ul li').on('click', function(){

        var panelToShow = $(this).data('panel');

        $(this).parent().find('.active').removeClass('active');
        $(this).addClass('active');

        $(this).closest('.srv-tabs-container').find('.panel.active').removeClass('active');
        $('#'+panelToShow).addClass('active');

    });

    $('.our-services-section .panel .mobile-header').on('click', function(){
        $(this).parent().toggleClass('mobile-active');
    });


    positionRectangle();
    $('.srv-nav li').on('click', positionRectangle);
    $('.srv-nav ul').on('mouseleave', positionRectangle);

    $('.srv-nav ul').on('mousemove', function(e){
        if(!isMob){
            var left = e.pageX - $('.srv-nav ul').offset().left - 55;
            if(left <= liMarg){
                left = liMarg;
            } else if(left >= (3 * 110) + (7 * liMarg) ){
                left = (3 * 110) + (7 * liMarg);
            }
            $('.line').css('left', left);    
        }    
    });

});


$(window).on('resize', function(){
    positionRectangle();

    liMarg = 70;
    isMob = 0;

    if($(window).width() < 1200){
        liMarg = 60;
        isMob = 1;
    }

    if($(window).width() < 992){
        liMarg = 30;
    } 
});


