    .stamped-photo-wrapper {
        display: inline-block;
        background: #f5f5f5;
        margin: 0 0 1em;
        width: 100%;
        box-sizing: border-box;
        -moz-box-sizing: border-box;
        -webkit-box-sizing: border-box;
        box-shadow: 0px 1px 1px 0px rgba(0, 0, 0, 0.18);
        border-radius: 3px;
        -moz-border-radius: 3px;
        -webkit-border-radius: 3px;
    }

    .stamped-reviews-rating {
        margin: 5px 0px;
    }

    .stamped-reviews-author {
        font-weight: bold;
    }

    .stamped-reviews-location {
        color: #777;
        font-size: 12px;
    }

    .stamped-reviews-message-image-block {
        position: relative;
        margin-top: 0px !important;
    }

    .stamped-photo-wrapper:hover {
        box-shadow: 0px 2px 2px 0px rgba(0, 0, 0, 0.18);
    }

    .stamped-reviews-holder {
        padding: 10px 12px;
        font-weight: normal;
    }

    .stamped-reviews-photo-link:first-child {
        display: block !important;
    }

    .stamped-reviews-message-image-block a:first-child {
        display: block !important;
    }

    .stamped-reviews-message-image-block[data-type="video"]:before {
        content: '►';
        width: 100%;
        background: rgba(0, 0, 0, 0.3);
        color: white;
        font-size: 30px;
        position: absolute;
        height: 100%;
        vertical-align: middle;
        text-align: center;
        display: flex;
        align-items: center;
        justify-content: center;
    }

