        font-size: 13px;
        margin-top: 20px;
        border-top: 1px solid #eee;
        padding-top: 10px;
        padding-bottom: 20px;
    }

        .stamped-reviews-image-wrapper span {
            font-size: 12px;
            cursor: pointer;
        }

    .fa-star, .fa-star-o, .fa-star-half-o {
        color: gold;
    }

    #stamped-reviews-widget[data-widget-type="wall-photos"] .stamped-pagination span {
        font-size: 15px;
    }

    #stamped-reviews-widget[data-widget-type="wall-photos"] span#stamped-pagination-next a:before {
        content: '>';
    }

    #stamped-reviews-widget[data-widget-type="wall-photos"] span#stamped-pagination-prev a:before {
        content: '<';
    }

