    next {
        position: initial;
    }

    .stamped-reviews-message-image-block a:before {
        content: '';
        font-size: 20px;
        letter-spacing: 3px;
        position: relative;
        overflow: hidden;
        position: absolute;
        width: 100%;
        height: 100%;
        background: rgba(0, 0, 0, 0.5);
        color: #fff;
        text-align: center;
        opacity: 0;
        display: flex;
        align-items: center;
        justify-content: center;
        transition: all 0.3s;
    }

